graphql.query:
query {
  _customers {
    customernumber
    customername
    contactfirstname
    contactlastname
    city
    country
  }
}

query GetCustomerWithOrders($id: Int!) {
  _customer(id: $id) {
    customernumber
    customername
    orders {
      ordernumber
      orderdate
      status
    }
  }
}

query GetCustomerFull($id: Int!) {
  _customer(id: $id) {
    customernumber
    customername
    orders {
      ordernumber
      orderdate
      status
      orderdetails {
        productcode
        quantityordered
        priceeach
      }
    }
  }
}

query GetCustomerWithPayments($id: Int!) {
  _customer(id: $id) {
    customernumber
    customername
    payments {
      checknumber
      paymentdate
      amount
    }
  }
}

query GetCustomerComplete($id: Int!) {
  _customer(id: $id) {
    customernumber
    customername
    contactfirstname
    contactlastname
    phone
    city
    country
    orders {
      ordernumber
      orderdate
      requireddate
      shippeddate
      status
      comments
      orderdetails {
        orderlinenumber
        productcode
        quantityordered
        priceeach
      }
    }
    payments {
      checknumber
      paymentdate
      amount
    }
  }
}

query GetCustomerOrders($customerId: Int!) {
  _customerOrders(customerId: $customerId) {
    ordernumber
    orderdate
    requireddate
    shippeddate
    status
    comments
  }
}

query GetCustomerOrdersWithDetails($customerId: Int!) {
  _customerOrders(customerId: $customerId) {
    ordernumber
    orderdate
    status
    orderdetails {
      orderlinenumber
      productcode
      quantityordered
      priceeach
    }
  }
}

query GetOrderDetails($orderNumber: Int!) {
  _orderDetails(orderNumber: $orderNumber) {
    ordernumber
    productcode
    quantityordered
    priceeach
    orderlinenumber
  }
}

query GetCustomerPayments($customerId: Int!) {
  _customerPayments(customerId: $customerId) {
    customernumber
    checknumber
    paymentdate
    amount
  }
}

query GetProductLine($productLine: String!) {
  _productLine(productLine: $productLine) {
    productline
    textdescription
    htmldescription
  }
}

query GetProductLineWithProducts($productLine: String!) {
  _productLine(productLine: $productLine) {
    productline
    textdescription
    products {
      productcode
      productname
      productscale
      productvendor
      quantityinstock
      buyprice
      msrp
    }
  }
}

query GetProductLineProducts($productLine: String!) {
  _productLineProducts(productLine: $productLine) {
    productcode
    productname
    productscale
    productvendor
    quantityinstock
    buyprice
    msrp
  }
}

query {
