graphql.query:
query GetProductLines {
  productLines {
    productline
    textdescription
    htmldescription
    image
    products {
      productcode
      productname
      productline
      productscale
      productvendor
      productdescription
      quantityinstock
      buyprice
      msrp
    }
  }
}

query GetProductLinesBasic {
  productLines {
    productline
    textdescription
    htmldescription
    image
  }
}
